	private Character calculateMostCommon(List<String> input, int i) {
		int[] counts = new int[2];
		for (String s : input) {
			if (s.charAt(i) == '1') counts[1]++;
			else counts[0]++;
		}
		if (counts[1] > counts[0]) return '1';
		if (counts[0] > counts[1]) return '0';
		return null;
	}

	private String filterOxygen(List<String> oxygenList, int i, int numlen) {
		if (oxygenList.size() == 1) return oxygenList.get(0);
		if (i == numlen) return null;

		Character mostCommon = calculateMostCommon(oxygenList, i);
		List<String> filteredList = oxygenList.stream().filter(s -> {
			if (mostCommon != null) return s.charAt(i) == mostCommon;
			return s.charAt(i) == '1';
		}).collect(Collectors.toList());
		System.out.printf("Oxygen filteredList: %s\n", filteredList);

		return filterOxygen(filteredList, i + 1, numlen);
	}

	private String filterCo2(List<String> co2List, int i, int numlen) {
		if (co2List.size() == 1) return co2List.get(0);
		if (i == numlen) return null;

		Character mostCommon = calculateMostCommon(co2List, i);
		List<String> filteredList = co2List.stream().filter(s -> {
			if (mostCommon != null) return s.charAt(i) != mostCommon;
			return s.charAt(i) == '0';
		}).collect(Collectors.toList());
		System.out.printf("Co2 filteredList: %s, mostcommon: %c\n", filteredList, mostCommon);

		return filterCo2(filteredList, i + 1, numlen);
	}
